<?php/** * Class Users */class Users extends Controller{    public function userLogin()    {        $email = $_POST['email'];        $password = md5($_POST['password']);        $users_model = $this->loadModel('usersmodel');        $users_model->userLogin($email, $password);    }    public function userLogOut()    {        session_start();        session_destroy();        if (isset($_SERVER['HTTP_REFERER'])) {            header('Location: ' . $_SERVER['HTTP_REFERER']);        } else {            header('Location: ' . URL . '/home');        }        exit;    }}